# Funci칩n para cargar el texto del PDF
def extraer_texto_pdf(archivo):
    texto = ""
    if archivo:
        with tempfile.NamedTemporaryFile(delete=False) as temp_file:
            temp_file.write(archivo.read())
            temp_file_path = temp_file.name
        with open(temp_file_path, 'rb') as file:
            reader = PyPDF2.PdfReader(file)
            for page in range(len(reader.pages)):
                texto += reader.pages[page].extract_text()
        os.unlink(temp_file_path)  # Eliminar el archivo temporal despu칠s de usarlo
    return texto

# Funci칩n para preprocesar texto
def preprocesar_texto(texto):
    tokens = word_tokenize(texto, language='spanish')
    tokens = [word.lower() for word in tokens if word.isalpha()]
    stopwords_es = set(stopwords.words('spanish'))
    tokens = [word for word in tokens if not word in stopwords_es]
    stemmer = SnowballStemmer('spanish')
    tokens = [stemmer.stem(word) for word in tokens]
    return " ".join(tokens)


# Funci칩n para extraer datos de un CSV
def extraer_texto_csv(archivo):
    """
    Extrae el contenido de un archivo CSV y lo convierte en texto legible.
    Par치metros:
    - archivo: archivo subido por el usuario.
    
    Retorna:
    - str: Texto representando el contenido del CSV.
    """
    try:
        # Leer el archivo CSV con pandas
        df = pd.read_csv(archivo)
        # Convertir el DataFrame a texto legible
        texto_csv = df.to_string(index=False)
        return texto_csv
    except Exception as e:
        return f"Error al procesar el archivo CSV: {e}"

# Funci칩n para preprocesar el texto del CSV
def preprocesar_texto_csv(texto_csv):
    """
    Preprocesa el texto extra칤do del CSV.
    Elimina stopwords, convierte palabras a min칰sculas y aplica stemming.
    
    Par치metros:
    - texto_csv: str, texto extra칤do del archivo CSV.
    
    Retorna:
    - str: Texto preprocesado.
    """
    try:
        # Tokenizaci칩n del texto
        tokens = word_tokenize(texto_csv, language='spanish')
        # Filtrar solo palabras alfab칠ticas y convertirlas a min칰sculas
        tokens = [word.lower() for word in tokens if word.isalpha()]
        # Eliminar stopwords
        stopwords_es = set(stopwords.words('spanish'))
        tokens = [word for word in tokens if word not in stopwords_es]
        # Aplicar stemming
        stemmer = SnowballStemmer('spanish')
        tokens = [stemmer.stem(word) for word in tokens]
        # Unir los tokens en un solo texto
        return " ".join(tokens)
    except Exception as e:
        return f"Error durante el preprocesamiento del texto CSV: {e}"


# Funci칩n para obtener respuesta de OpenAI

def obtener_respuesta(pregunta, contexto="", modelo="gpt-4"):
    
  
    
    try:
        import openai
        openai.api_key = "API_KEY"
        # Construir el mensaje para el modelo
        mensajes = [
            {
                "role": "system",
                "content": (
                    "Eres MikhailAI, el asistente virtual del alcalde de Tunja. "
                    "eres el Alcalde de la ciudad de Tunja, ten un tono amable y amplio en tus respuestas,"
                    "Tu prop칩sito es brindar informaci칩n oficial sobre la alcald칤a, "
                    "incluyendo tr치mites, eventos comunitarios, turismo, pico y placa, "
                    "y cualquier tema relacionado con los servicios municipales. "
                    "Responde de manera clara y profesional, adapt치ndote al contexto local."
                )
            }
        ]
        if contexto:
            mensajes.append({"role": "system", "content": f"Contexto adicional:\n{contexto}"})
        mensajes.append({"role": "user", "content": pregunta})
        
        # Solicitar respuesta al modelo de OpenAI
        respuesta = openai.ChatCompletion.create(
            model=modelo,
            messages=mensajes
        )
        return respuesta.choices[0].message["content"].strip()
    except Exception as e:
        st.error(f"Error al obtener la respuesta del modelo: {e}")
        return "No se pudo obtener la respuesta en este momento."

